INSERT INTO `concerts` (`id`, `title`, `date`, `description`, `place`, `ticket`, `hidden`) VALUES (8,'Международный день музыки: Гала-концерт, посвящённый открытию 83-го филармонического сезона','2020-10-01 19:00:00','Государственный академический симфонический оркестр Республики Беларусь,\r\nГосударственная академическая хоровая капелла им. Г.Ширмы, Государственный камерный хор Республики Беларусь, Государственный камерный оркестр Республики Беларусь\r\n\r\nВ программе первого отделения:\r\nП.Чайковский-С.Рахманинов Колыбельная \r\nН.Паганини-Э.Изаи Вариации \r\nМ.Палмери Магнифика с камерным хором, банденеоном, фортепиано сопрано меццо-сопрано\r\n\r\nДирижёр - заслуженный артист России Евгений Бушков\r\n','Большой зал БГФ','https://tce.by/shows.html?base=Nzk0QjIxQ0QtRjE5QS02NzQ3LTlBNTAtN0ZCNkE5MDI2NUJE&data=5731',0),(9,'Цикл камерных концертов «Хорошо… без дирижёра» (концерт первый)','2021-03-16 19:00:00','В программе:\r\nА.Бородин - Вариации на тему русской народной песни &quot;Чем я тебя огорчила&quot;\r\nИсп.: Олег Подов, Мария Ракель, Николай Киселев\r\nА.Бородин  - Струнный квартет\r\nИсп.: Павел Батян, Кузьма Папакуль, Игорь Цховребов, Никита Кравец\r\nА.Глазунов - Вариации на русскую тему из сюиты ор.35\r\nИсп.: Олег Подов, Мария Ракель, Елена Сердюкова, Николай Киселев \r\nМ.Глинка - Большой секстет ми бемоль мажор\r\nИсп.: Олег Подов, Мария Ракель, Елена Сердюкова, Николай Киселев, Михаил Николайчик, Мария Ромейко ','Малый зал БГФ','https://tce.by/shows.html?base=Nzk0QjIxQ0QtRjE5QS02NzQ3LTlBNTAtN0ZCNkE5MDI2NUJE&data=5724',0),(10,'Абонемент №5 «Классика – это классно»','2020-09-26 15:00:00','Концерт третий «ЮМОР В МУЗЫКЕ» \r\n\r\nЗаслуженный коллектив Республики Беларусь\r\nГосударственный камерный оркестр Республики Беларусь\r\n\r\nГлавный дирижёр – заслуженный артист Российской Федерации\r\nЕвгений Бушков','Большой зал БГФ','https://tce.by/shows.html?base=Nzk0QjIxQ0QtRjE5QS02NzQ3LTlBNTAtN0ZCNkE5MDI2NUJE&data=5645',0),(11,'Абонемент № 2 «Дебют юных талант